import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import AuthService from '../../authService';
import './navigationBarStyles.css';

const NavigationBar = () => {
  const [selectedOption, setSelectedOption] = useState('/');
  const navigate = useNavigate();
  const isLoggedIn = AuthService.isAuthenticated();

  const handleDropdownChange = (event) => {
    const selectedRoute = event.target.value;
    setSelectedOption(selectedRoute);
    navigate(selectedRoute);
  };

  return (
    <div className="navigation-bar-container">
      <div className="navbar-form">
        {/* Logo */}
        <div className="logo-container">
          <img src="/logoname.png" alt="Logo" className="logo" />
        </div>
        <div className="nav-dropdown-container">
          {isLoggedIn && (
            <Link to="/Home" className="home-link">Home</Link>
          )}
          {isLoggedIn && (
            <Link to="/OurTeam" className="home-link">OurTeam</Link>
          )}
          {/* Dropdown with the selected option */}
          <input class="dark-light" type="checkbox" id="dark-light" name="dark-light"/>
          <label for="dark-light"></label>

          <div class="light-back"></div> 

          <a href="https://front.codes/" class="logo" target="_blank">
            <img src="https://assets.codepen.io/1462889/fcy.png" alt="" />
          </a>

          <div class="sec-center">   
            <input class="dropdown" type="checkbox" id="dropdown" name="dropdown"/>
            <label class="for-dropdown" for="dropdown">Dropdown Menu <i class="uil uil-arrow-down"></i></label>
            <div class="section-dropdown"> 
              <a href="#">Dropdown Link <i class="uil uil-arrow-right"></i></a>
              <input class="dropdown-sub" type="checkbox" id="dropdown-sub" name="dropdown-sub"/>
              <label class="for-dropdown-sub" for="dropdown-sub">Dropdown Sub <i class="uil uil-plus"></i></label>
              <div class="section-dropdown-sub"> 
                <a href="#">Dropdown Link <i class="uil uil-arrow-right"></i></a>
                <a href="#">Dropdown Link <i class="uil uil-arrow-right"></i></a>
              </div>
              <a href="#">Dropdown Link <i class="uil uil-arrow-right"></i></a>
              <a href="#">Dropdown Link <i class="uil uil-arrow-right"></i></a>
            </div>
          </div>
          {/* Render "Logout" outside the dropdown for logged-in users */}
          {isLoggedIn && (
            <Link to="/logout" className="logout-link">Logout</Link>
          )}
        </div>
      </div>
    </div>
  );
};

export default NavigationBar;
