import React, { useState } from 'react';
import './styles.css';

const ElectionsList = ({
  electionid,
  title,
  description,
  startdate,
  enddate,
}) => {
  const [isButtonExpanded, setIsButtonExpanded] = useState(false);

  const handleButtonClick = () => {
    // Your logic for the button click goes here
    console.log('Apply for promotion on this election');
  };

  return (
    <div className="electionlist-form-container">
      <div className="electionlist-form">
        <div className="icon-container">
          <div className="icon-circle"></div>
          <div className="form-icon">
            <span>
              <i className="icon icon-user"></i>
            </span>
          </div>
        </div>
        <div className="form-group">
          <label htmlFor="title">Title:</label>
          <div className="form-control item" id="title">
            {title}
          </div>
        </div>
        {/* ... (other form groups) */}
        <div className="form-group">
          <button
            type="button"
            className="btn btn-block create-account"
            onClick={() => console.log('Go vote')}
          >
            Go vote
          </button>
        </div>
        <div className="expand-button-container">
          <button
            type="button"
            className={`expand-button ${isButtonExpanded ? 'expanded' : ''}`}
            onClick={handleButtonClick}
            onMouseEnter={() => setIsButtonExpanded(true)}
            onMouseLeave={() => setIsButtonExpanded(false)}
          >
            Apply for Promotion
          </button>
        </div>
        <div className="social-media">
          <h5>Follow us on social media</h5>
          {/* ... (social media icons) */}
        </div>
      </div>
    </div>
  );
};

export default ElectionsList;
