import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import apiService from '../../apiService';
import CandidateForm from './candidateForm';
import './parent.css';

const CandidatesParentComponent = () => {
  const { electionid } = useParams();
  const [candidates, setCandidates] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('authToken');
        const data = await apiService.getcandidates(electionid, token);
        setCandidates(data);
      } catch (error) {
        console.error('Error fetching candidates:', error);
      }
    };

    fetchData();
  }, [electionid]);

  const handleVoteClick = async (candidateId) => {
    try {
      const token = localStorage.getItem('authToken');
      // You may need to adjust the following line based on your candidate data structure
      const candidate = candidates.find((c) => c.CandidateID === candidateId);
      if (candidate) {
        await apiService.voteforcandidate(candidate.CandidateID, electionid, token);
        console.log(`Voted for candidate: ${candidate.CandidateName}`);
        // You can update the UI or state based on the vote success
      }
    } catch (error) {
      console.error('Failed to vote for candidate:', error);
      // Handle error, show a message, etc.
    }
  };

  return (
    <div>
      <div className="election-id-container"></div>
      <div className="candidates-container">
        {candidates.map((candidate) => (
          <CandidateForm
            key={candidate.CandidateID}
            electionName={candidate.ElectionName}
            fullName={candidate.CandidateName}
            description={candidate.CandidateDetails}
            onVoteClick={() => handleVoteClick(candidate.CandidateID)}
          />
        ))}
      </div>
    </div>
  );
};

export default CandidatesParentComponent;
