// CandidatesParentComponent.js

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import apiService from '../../apiService';
import CandidateForm from './CandidateForm';
import './parent.css';

const CandidatesParentComponent = () => {
  const { electionid } = useParams();
  const [candidates, setCandidates] = useState([]);
  const [showAlreadyVotedWarning, setShowAlreadyVotedWarning] = useState(false);
  const [userVotingStatus, setUserVotingStatus] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('authToken');
        const data = await apiService.getcandidates(electionid, token);
        setCandidates(data);
      } catch (error) {
        console.error('Error fetching candidates:', error);
      }
    };

    fetchData();
  }, [electionid]);

  useEffect(() => {
    const fetchUserVotingStatus = async () => {
      try {
        const token = localStorage.getItem('authToken');
        const userId = getUserId(); // Replace this with your actual function to get user ID
        const data = await apiService.uservoted(userId, token);
        setUserVotingStatus(data);
      } catch (error) {
        console.error('Error fetching user voting status:', error);
      }
    };

    fetchUserVotingStatus();
  }, []); // Fetch user voting status when the component mounts

  const getUserId = () => {
    // Replace this with your actual function to get user ID from your authentication system
    // Example: return the user ID from your authentication context or state
    return '123'; // Replace '123' with your actual user ID retrieval logic
  };

  const handleVoteClick = async (electionId, candidateId) => {
    try {
      const token = localStorage.getItem('authToken');
      await apiService.voteforcandidate(candidateId, electionId, token);
      // You can update the UI or state based on the vote success
    } catch (error) {
      if (error.response && error.response.status === 400) {
        // Display warning for already voted
        setShowAlreadyVotedWarning(true);
      } else {
        console.error('Failed to vote for candidate:', error);
        // Handle other errors, show a message, etc.
      }
    }
  };

  const closeAlreadyVotedWarning = () => {
    setShowAlreadyVotedWarning(false);
  };

  return (
    <div>
      <div className="election-id-container">
        {/* Render the election ID or any additional information */}
      </div>
      <div className="candidates-container">
        {candidates.map(candidate => (
          <CandidateForm
            key={candidate.CandidateID}
            electionName={candidate.ElectionName}
            fullName={candidate.CandidateName}
            description={candidate.CandidateDetails}
            onVoteClick={() => handleVoteClick(electionid, candidate.CandidateID)}
            disableVoteButton={userVotingStatus && userVotingStatus.hasVoted}
            highlightVotedCandidate={userVotingStatus && userVotingStatus.candidateID === candidate.CandidateID}
          />
        ))}
      </div>

      {/* Display warning modal or alert */}
      {showAlreadyVotedWarning && (
        <div className="warning-modal">
          <p>You have already voted for this election!</p>
          <button onClick={closeAlreadyVotedWarning}>Close</button>
        </div>
      )}
    </div>
  );
};

export default CandidatesParentComponent;
