// authService.js
import { useDispatch } from 'react-redux';
import { setAuthToken, clearAuthToken } from './authActions';

const API_BASE_URL = 'http://13.39.23.248:3000/api';

const authService = {
  login: async (identifier, password) => {
    try {
      const response = await fetch(`${API_BASE_URL}/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*', // Adjust based on your server's requirements
        },
        body: JSON.stringify({ identifier, password }),
        credentials: 'include', // Include credentials (cookies, etc.)
      });

      if (!response.ok) {
        throw new Error('Login failed');
      }

      const data = await response.json();
      console.log('Login successful:', data);

      // Dispatch action to set the authentication token
      const dispatch = useDispatch();
      dispatch(setAuthToken(data.token));

      // You can handle the response data as needed
      // For example, you might store user information in state or context

    } catch (error) {
      console.error('Login failed:', error.message);
      throw error;
    }
  },

  logout: async () => {
    try {
      // Perform any additional logout logic if needed

      // Dispatch action to clear the authentication token
      const dispatch = useDispatch();
      dispatch(clearAuthToken());

      // You can handle the response data as needed

    } catch (error) {
      console.error('Logout failed:', error.message);
      throw error;
    }
  },

  register: async (username, email, password) => {
    try {
      const response = await fetch(`${API_BASE_URL}/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*', // Adjust based on your server's requirements
        },
        body: JSON.stringify({ username, password, email }),
      });

      if (!response.ok) {
        throw new Error('Registration failed');
      }

      const data = await response.json();
      console.log('Registration successful:', data);

      // You can handle the response data as needed
      // For example, you might store user information in state or context

    } catch (error) {
      console.error('Registration failed:', error.message);
      throw error;
    }
  }
};

export default authService;
