import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './styles.css';
import apiService from '../../apiService.js';

const ElectionForm = ({
  electionid,
  title,
  description,
  startdate,
  enddate,
}) => {
  const [isButtonExpanded, setIsButtonExpanded] = useState(false);
  const [isButtonClicked, setIsButtonClicked] = useState(false);
  const navigate = useNavigate();

  const handleButtonClick = async () => {
    try {
      const userData = await apiService.getelections(electionid);
    } catch (error) {
      console.log(error);
    }
    console.log('done');
    // Handle button click logic here
    setIsButtonClicked(!isButtonClicked);
    setIsButtonExpanded(!isButtonExpanded);
    navigate('/candform');
  };

  return (
    <div className="electionlist-form-container">
      <div className="electionlist-form">
        {/* ... Same structure as ElectionsList */}
        <div className="expand-button-container">
          <button
            type="button"
            className={`expand-button ${isButtonExpanded ? 'expanded' : ''} ${isButtonClicked ? 'clicked' : ''}`}
            onClick={handleButtonClick}
            onMouseEnter={() => setIsButtonExpanded(true)}
            onMouseLeave={() => setIsButtonExpanded(false)}
          >
            {isButtonExpanded ? 'Apply for Promotion' : 'AFP'}
          </button>
        </div>
        {/* ... Render election information */}
        <div className="form-group">
          <label htmlFor="title">Title:</label>
          <div className="form-control item" id="title">
            {title}
          </div>
        </div>
        <div className="form-group">
          <label htmlFor="description">Description:</label>
          <div className="form-control item" id="description">
            {description}
          </div>
        </div>
        <div className="form-group">
          <label htmlFor="startdate">Start Date:</label>
          <div className="form-control item" id="startdate">
            {startdate}
          </div>
        </div>
        <div className="form-group">
          <label htmlFor="enddate">End Date:</label>
          <div className="form-control item" id="enddate">
            {enddate}
          </div>
        </div>
        <div className="form-group">
          <button
            type="button"
            className="btn btn-block create-account"
            onClick={() => navigate('/candform')}
          >
            Go vote
          </button>
        </div>
        {/* ... Social media links */}
      </div>
    </div>
  );
};

const ElectionsList = () => {
  const navigate = useNavigate();
  const storedJsonString = localStorage.getItem('data');
  const retrievedArray = JSON.parse(storedJsonString);

  useEffect(() => {
    // Fetch elections or perform any necessary actions on component mount
    // Example: apiService.getElections()
  }, []); // Empty dependency array ensures the effect runs once on mount

  return (
    <div>
      {retrievedArray.map((election) => (
        <ElectionForm
          key={election.electionid}
          electionid={election.electionid}
          title={election.title}
          description={election.description}
          startdate={election.startdate}
          enddate={election.enddate}
        />
      ))}
    </div>
  );
};

export default ElectionsList;
