// ProtectedRoute.js
import React, { useEffect, useState } from 'react';
import { Route, Navigate } from 'react-router-dom';
import AuthService from './authService';

const ProtectedRoute = ({ element }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(null); // Use null to indicate that the authentication status is being checked

  useEffect(() => {
    const checkAuthentication = async () => {
      try {
        const isAuthenticated = await AuthService.isAuthenticated();
        setIsLoggedIn(isAuthenticated);
      } catch (error) {
        console.error('Error checking authentication status:', error);
        setIsLoggedIn(false);
      }
    };

    checkAuthentication();
  }, []);

  if (isLoggedIn === null) {
    // Still checking authentication status, you can show a loading indicator or redirect to a loading page
    return <div>Loading...</div>;
  }

  return isLoggedIn ? <Route element={element} /> : <Navigate to="/notloggedin" />;
};

export default ProtectedRoute;
