import React, { useState, useEffect } from 'react';
import apiService from '../../apiService';
import CandidatesList from './candidatesList';

const CandidatesParentComponent = ({ electionId }) => {
  const [candidates, setCandidates] = useState([]);

  useEffect(() => {
    const fetchCandidates = async () => {
      try {
        const token = localStorage.getItem('authToken');

        // Fetch candidates for the specified election
        const data = await apiService.getCandidates(electionId, token);
        setCandidates(data);

        console.log('Candidates Data:', data);
      } catch (error) {
        console.error('Error fetching candidates:', error);
      }
    };

    fetchCandidates();
  }, [electionId]); // Trigger the effect when the electionId changes

  return (
    <div className="candidates-container">
      {candidates.map(candidate => (
        <CandidatesList
          key={candidate.id} // Replace with the actual candidate ID
          name={candidate.name}
          party={candidate.party}
          // Add other candidate details as needed
        />
      ))}
    </div>
  );
};

export default CandidatesParentComponent;
