// ElectionsList.js
import React, { useState } from 'react';
import './styles.css';
import authService from '../../authService.js';

const ElectionsList = ({ elections, handleCreateAccount }) => {
  return (
    <div>
      {elections.map((election) => (
        <div key={election.id} className="elections-form-container">
          <div className="electionslist-form">
            <form>
              <div className="icon-container">
                <div className="icon-circle"></div>
                <div className="form-icon">
                  <img
                    src={`${process.env.PUBLIC_URL}/manual-voting.png`}
                    alt="Vote Icon"
                    width="40"
                    height="40"
                  />
                </div>
              </div>
              <div className="form-group">
                <input
                  type="text"
                  className="form-control item"
                  id={`electionCode_${election.id}`}
                  placeholder="Election Code"
                  value={election.code}
                  readOnly
                />
              </div>
              <div className="form-group">
                <button
                  type="button"
                  className="btn btn-block create-account"
                  onClick={() => handleCreateAccount(election.code)}
                >
                  Register to the election
                </button>
              </div>
            </form>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ElectionsList;
