// CandidatesParentComponent.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import apiService from '../../apiService';
import CandidateForm from './candidateForm';

const CandidatesParentComponent = () => {
  const { electionid } = useParams();
  const [candidates, setCandidates] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('authToken');
        const data = await apiService.getcandidates(electionid, token);
        setCandidates(data);
      } catch (error) {
        console.error('Error fetching candidates:', error);
      }
    };

    fetchData();
  }, [electionid]);

  const handleVoteClick = async (candidateInfo) => {
    try {
      const token = localStorage.getItem('authToken');
      const { electionName, fullName, description } = candidateInfo;
      const response = await apiService.voteforcandidate(electionid, fullName, token);

      // Assuming the response includes some information about the vote
      console.log('Vote response:', response);

      // You may want to update the UI or state based on the vote response
    } catch (error) {
      console.error('Failed to vote for candidate:', error);
      // Handle error, show a message, etc.
    }
  };

  return (
    <div>
      <h2>Candidates for Election ID: {electionid}</h2>
      <div className="candidates-container">
        {candidates.map(candidate => (
          <CandidateForm
            key={candidate.CandidateID}
            electionName={candidate.ElectionName}
            fullName={candidate.CandidateName}
            description={candidate.CandidateDetails}
            onVoteClick={handleVoteClick}
          />
        ))}
      </div>
    </div>
  );
};

export default CandidatesParentComponent;
