// CandidatesParentComponent.js

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import apiService from '../../apiService';
import CandidatesList from './candidatesList';
import { useLocation } from 'react-router-dom';


const CandidatesParentComponent = () => {
  const { electionId } = useParams();
  const [candidates, setCandidates] = useState([]);
  const electionid = localStorage.getItem('ElectionID');

  console.log('Election ID:', electionId)

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('authToken');
        const data = await apiService.getcandidates(electionId, token);
        setCandidates(data);
        console.log('Candidates Data:', data);
      } catch (error) {
        console.error('Error fetching candidates:', error);
      }
    };

    fetchData();
  }, [electionId]);

  return (
    <div>
      <h2>Candidates for Election ID: {electionId}</h2>
      {candidates.map(candidate => (
        <CandidatesList
          key={candidate.CandidateID}
          candidateId={candidate.CandidateID}
          name={candidate.Name}
          party={candidate.Party}
          // Add other candidate properties as needed
        />
      ))}
    </div>
  );
};

export default CandidatesParentComponent;
