// ElectionResults.js

import React, { useState, useEffect } from 'react';
import apiService from '../../apiService';

const ElectionResults = ({ electionid }) => {
  const [results, setResults] = useState([]);

  useEffect(() => {
    const fetchResultsData = async () => {
      try {
        const userID = localStorage.getItem('ID');
        const response = await apiService.results(userID);

        // Ensure the electionid is a number (use parseInt or Number)
        const electionIDToFilter = parseInt(electionid);

        const filteredResults = response.filter(candidate => candidate.ElectionID === electionIDToFilter);
        setResults(filteredResults);
      } catch (error) {
        console.error('Error fetching results:', error);
      }
    };

    fetchResultsData();
  }, [electionid]);

  if (!results || results.length === 0) {
    return <div>No results available for this election.</div>;
  }

  return (
    <div className="results-container">
      {results.map((candidate, index) => (
        <div key={index} className="candidate-card">
          <div className="candidate-name">{candidate.CandidateName}</div>
          <div className="election-name">{`Election: ${candidate.ElectionName}`}</div>
          <div className="votes-count">{`Votes: ${candidate.VotesCount || 0}`}</div>
        </div>
      ))}
    </div>
  );
};

export default ElectionResults;
