// ParentComponent.js
import React, { useState, useEffect } from 'react';
import apiService from '../../apiService';
import ElectionsList from './electionList';
import './parentComponent.css';

const ParentComponent = () => {
  const [electionsData, setElectionsData] = useState([]);
  const [appliedElections, setAppliedElections] = useState([]); // Initialize as an empty array

  const fetchData = async () => {
    try {
      const userId = localStorage.getItem('ID');
      const token = localStorage.getItem('authToken');
      const data = await apiService.getelections(userId, token);
      setElectionsData(data);

      // Fetch applied elections
      const appliedElectionsData = await apiService.getelectionsapplied(token);
      setAppliedElections(appliedElectionsData); // Ensure to set appliedElectionsData.elections
    } catch (error) {
      console.error('Error fetching elections:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="elections-container">
      {electionsData.map((election, index) => (
        <ElectionsList
          key={election.ElectionID}
          electionid={election.ElectionID}
          title={election.Title}
          description={election.Description}
          startdate={election.StartDate}
          enddate={election.EndDate}
          isLastInRow={(index + 1) % 3 === 0}
          isApplied={appliedElections.includes(election.ElectionID)}
        />
      ))}
    </div>
  );
};

export default ParentComponent;
